import { Canvas } from '@react-three/fiber';
import { Suspense } from 'react';

import AnimatedCamera from './AnimatedCamera';
import styles from '../ProjectCard/ProjectCard.module.css';
import AnimatedAsset from './AnimatedAsset';

function Scene() {
  return (
    <div className={styles.canvas_wrapper}>
      <Canvas>
        {/* Camera ðŸŽ¥ */}
        <AnimatedCamera />

        {/* Lights ðŸ’¡ */}
        <ambientLight intensity={1} />
        {/* <pointLight position={[4, 4, 2]} intensity={4} /> */}
        <directionalLight color="white" position={[0, 0, 5]} />

        {/* We can create a background color as a child element of the canvas we just have to attach it */}
        <color args={['white']} attach="background" />

        {/* Objects ðŸ“¦ */}
        <Suspense fallback={null}>
          <AnimatedAsset
            modelUrl={
              '/Users/chengzixin/Documents/ITP/Second Year/2023 Fall/Visual Journalism/nypd-robot/prototype/public/k5_nypdrobot.glb'
            }
          />

          <meshStandardMaterial roughness={0.3} metalness={0.6} />
        </Suspense>
      </Canvas>
    </div>
  );
}

export default Scene;
