import styles from '../LandingPage.module.css';
import { useRef, useEffect } from 'react';
import { AnimationTimeline } from '../BackgroundScene/AnimationTimeline';

export default function HeightChart(props) {
  // const { left, width, top, header, paragraph } = props;
  const { imgSrc, startKey, fadeInKey, fadeOutKey } = props;
  const imgRef = useRef();

  // startKey={'fullshot1'}
  //       fadeInKey={'fullshot2'}
  //       fadeOutKey={'closeup'}

  useEffect(() => {
    AnimationTimeline.to(
      imgRef.current.style,
      {
        opacity: 0,
      },
      // startKey
      'fullshot1'
    );

    AnimationTimeline.to(
      imgRef.current.style,
      {
        opacity: 0,
      },
      // fadeInKey
      'fullshot2'
    );

    AnimationTimeline.to(
      imgRef.current.style,
      {
        opacity: 0,
      },
      // fadeOutKey
      'closeup'
    );
  }, [imgRef]);

  return console.log(imgRef);
  // <div ref={imgRef} className={styles.full_image}>
  //   {/* <img src={imgSrc} alt={'image'}></img> */}

  // </div>
}
