function SimpleSlide(props) {
  const { children, viewportPosition } = props;
  return (
    <h2
      style={{
        color: "#BFA998",
        position: "absolute",
        width: "100%",
        top: `${viewportPosition}vh`,
        zIndex: "50",
        textAlign: "center",
      }}
    >
      {children}
    </h2>
  );
}

export default SimpleSlide;
