import { Canvas } from '@react-three/fiber';
import { PerspectiveCamera, Sparkles } from '@react-three/drei';
import { Suspense } from 'react';

import SimpleSlide from './SimpleSlide';
import SimpleGLTFAsset from './SimpleGLTFAsset';
import AnimatedCamera from './AnimatedCamera';

function Scene() {
  return (
    <section id="canvas_wrapper">
      <Canvas>
        {/* Camera ðŸŽ¥ */}
        <AnimatedCamera />
        {/* Lights ðŸ’¡ */}
        <ambientLight intensity={5} />
        <pointLight position={[4, 4, 2]} intensity={4} />

        {/* We can create a background color as a child element of the canvas we just have to attach it */}
        <color args={['grey']} attach="background" />

        {/* Objects ðŸ“¦ */}
        <Suspense fallback={null}>
          <SimpleGLTFAsset
            modelUrl={'https://cdn.glitch.global/c030b272-a13c-4e68-a7c8-43f0704031d1/eve.glb?v=1698089903344'}
          />
        </Suspense>
      </Canvas>
    </section>
  );
}

export default Scene;
