export const PROJECT_LIST = [
  {
    title: 'WPP House',
    imgSrc: require('../assets/wpphouse_cover.png'),
    pgSrc: '/wpp-house',
    description: 'Creative Coding & Web Design',
    ingredients: [
      { measure: '2 slices', name: 'Bread' },
      { measure: '2 slices', name: 'Cold salted butter' },
    ],
    instructions: ['Cut off the crust of the bread.', 'Toast the bread to your liking.'],
  },
  {
    title: 'Lucky Tiger',
    imgSrc: require('../assets/luckytiger_cover.jpeg'),
    pgSrc: '/lucky-tiger',
    description: 'Web Dev & Design',
    ingredients: [
      {
        name: 'boneless rib eye steak',
        measure: '1 1/2 pounds',
      },
      {
        name: 'small pear, peeled and coarsely grated',
        measure: '1/2',
      },
    ],
    instructions: [
      'Wrap steak in plastic wrap, and place in the freezer for 30 minutes.',
      'In a medium bowl, combine pear, ginger and gochujang.',
    ],
  },
  {
    title: '2022 Demo Reel',
    imgSrc: require('../assets/2022demoreel_cover.png'),
    pgSrc: '/2022-demo-reel',
    description: 'Motion Design',
    ingredients: [
      { name: 'Milk powder', measure: '1 cup' },
      { name: 'All-purpose flour', measure: '1/4 cup' },
    ],
    instructions: [
      'In a mixing bowl, combine milk powder, all-purpose flour, ghee, milk, baking soda, and cardamom powder.',
      'Knead the mixture into a soft dough.',
      'Divide the dough into small portions and shape them into smooth balls.',
    ],
  },
  {
    title: 'TapType',
    imgSrc: require('../assets/taptype_cover.png'),
    pgSrc: '/taptype',
    description: 'Creative Coding',
    ingredients: [
      { measure: '3/4 cup', name: 'Melted butter' },
      { measure: '2 tbsp', name: 'Whole milk' },
    ],
    instructions: ['Melt butter', 'Beat lightly: milk, butter, egg', 'Add: flour, baking powder, sugar, salt', 'Mix'],
  },
];
